#Could also make the function so that user can enter there own values and then the attack will see if the attack can be performed

def modinv(a, m):
    #This portion calculates the extended Euclidian Algorithm (EGCD)
    #We make a list of quotients used to divide the number as it will be used when the algorithm works its way up
    quotients = list()
    b = m
    #This porition obtains the GCD for the two numbers a,b and appends b/a floored (i.e. b//a) to quotients list 
    while a != 0:
        quotients.append(b//a)
        #a becaome a%b, and and b becomes a, these operations occur until a gcd is found (i.e a ==0)
        a, b = b%a, a
    #we set g equal to the gcd, and then set x = 0 and y = 1 as we are going to work our way back up 
    #the equation, until we find coefficient that gives us ax = 1 mod n
    g, x, y = b,0,1
    for i in range(len(quotients)-1,-1,-1):
        x,y = y - quotients[i] * x, x
    #print(g,x,y)
    if g != 1:
        raise ValueError('Modular inverse does not exist.')
    else:
        return x % m


def attack(c1, c2, e1, e2, N):
    if math.gcd(e1, e2) != 1:
        raise ValueError("Exponents e1 and e2 must be coprime")
    s1 = modinv(e1,e2)
    s2 = (1 - e1 * s1) / e2
    #Equation C1^x + C2^y = M1^e1*x + M2^e2*y
    m1 = pow(c1,s1,N)
    m2 = pow(c2,int(s2),N)
    return (m1 * m2) % N


def encrypt(message):
    m_int = int(base64.b64encode(message.encode("ASCII")).hex(),base = 16)
    return m_int


